import ListHeader from "./components/ListHeader";
import ListItem from "./components/ListItem";
import {useEffect, useState} from 'react';

const App = ()=> {
  //states
  const [tasks, setTasks] = useState([]);
  const userEmail = "juansito@test.com";
  //function to get data from db
  const getData = async () => {

    try {
      await fetch(`http://localhost:8000/todos/${userEmail}`)
      .then((response) => response.json()


      );
    }
    catch(err){
      console.error(err);
    }
  }
  setTasks(response.json);
  useEffect(()=> getData, []); 

  //Sort by date  
  const sortedTasks = Array.from(tasks)?.sort((a,b)=> (new Date(a.date)) - (new Date(b.date)));

  return (
    <div className="app">
      <ListHeader listName={" 👨🏻‍💻 CS tick list"}/>
      {sortedTasks?.map((task) =><ListItem key={task.id} task={task} />)}
    </div>
  );
}

export default App;
