import { useState } from "react";

function Auth() {

  const [isLogIn, setIsLogIn] = useState(true);

  const [error, setError] = useState(null);

  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [confirmPassword, setConfirmPassword] = useState(null);

  const viewLogin = (status) => {
    setError(null);
    setIsLogIn(status);
  }

  const handleSubmit = async (e, endpoint) => {
    e.preventDefault();
    // sign up page
    if(!isLogIn && password!== confirmPassword) {
      setError("Passwords do not match!");
      return;
    }

    await fetch(`${process.env.REACT_APP_API_URL}/${endpoint}`, {
      method: 'POST',
      headers: {'content-type': 'application/json'},
      body: JSON.stringify({email, password})
    })
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
    })
  }

  return (
    <div className='auth-container'>
      <div className="auth-container-box">
        <form onSubmit={(e) => handleSubmit(e, isLogIn? '/login' : '/signup')}>
          <h2>{isLogIn ? "Please log in" : "Please sign up"}</h2>
          <input type="email" placeholder="email"/>
          <input type="password" placeholder="password"/>
          {!isLogIn && <input type="password" placeholder="confirm password"/>}
          <input type="submit" value={isLogIn? "Log in" : "Sign up"} className="create"/>
          {error && <p>{error}</p>}
        </form>
        <div className="auth-options">
          <button type="button"  
            onClick={() => viewLogin(false)}
            style= {{backgroundColor: !isLogIn? "white" : 'rgb(180, 180, 180)'}}
          > Sign Up </button>
          <button type="button"  
            onClick={() => viewLogin(true)}
            style= {{backgroundColor: isLogIn? "white" : 'rgb(180, 180, 180)'}}
          > Log In </button>
        </div>

      </div>
    </div>
  );
}

export default Auth;
